import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAccessibility, useAuth } from '../contexts/AllContexts';

function Settings() {
  const { settings, toggleHighContrast, toggleVoice, toggleScreenReader, toggleHaptic, toggleBiometric, setFontSize, setVoiceSpeed, setConfirmationDelay } = useAccessibility();
  const { updateAccessibilitySettings } = useAuth();
  const navigate = useNavigate();
  const [saving, setSaving] = useState(false);

  const handleSave = async () => {
    setSaving(true);
    await updateAccessibilitySettings(settings);
    setSaving(false);
  };

  return (
    <div style={{ padding: '20px', maxWidth: '600px' }}>
      <h2>Accessibility Settings</h2>

      <div style={{ marginTop: '30px' }}>
        <h3>Visual Settings</h3>
        <div style={{ marginTop: '15px' }}>
          <label>
            <input
              type="checkbox"
              checked={settings.highContrastMode}
              onChange={toggleHighContrast}
              style={{ marginRight: '10px' }}
            />
            High Contrast Mode
          </label>
        </div>

        <div style={{ marginTop: '15px' }}>
          <label>Font Size:</label>
          <select 
            value={settings.fontSize} 
            onChange={(e) => setFontSize(e.target.value)}
            style={{ marginLeft: '10px', padding: '5px' }}
          >
            <option value="small">Small</option>
            <option value="medium">Medium</option>
            <option value="large">Large</option>
            <option value="extra-large">Extra Large</option>
          </select>
        </div>
      </div>

      <div style={{ marginTop: '30px' }}>
        <h3>Voice Settings</h3>
        <label>
          <input
            type="checkbox"
            checked={settings.enableVoiceNavigation}
            onChange={toggleVoice}
            style={{ marginRight: '10px' }}
          />
          Enable Voice Navigation
        </label>
        <div style={{ marginTop: '15px' }}>
          <label>
            Voice Speed:
            <input
              type="range"
              min="0.5"
              max="2"
              step="0.1"
              value={settings.voiceSpeed}
              onChange={(e) => setVoiceSpeed(e.target.value)}
              style={{ marginLeft: '10px' }}
            />
            <span style={{ marginLeft: '10px' }}>{settings.voiceSpeed.toFixed(1)}x</span>
          </label>
        </div>
      </div>

      <div style={{ marginTop: '30px' }}>
        <h3>Screen Reader & Haptics</h3>
        <div style={{ marginTop: '10px' }}>
          <label>
            <input
              type="checkbox"
              checked={settings.enableScreenReader}
              onChange={toggleScreenReader}
              style={{ marginRight: '10px' }}
            />
            Screen Reader Mode
          </label>
        </div>
        <div style={{ marginTop: '10px' }}>
          <label>
            <input
              type="checkbox"
              checked={settings.hapticFeedback}
              onChange={toggleHaptic}
              style={{ marginRight: '10px' }}
            />
            Haptic Feedback (if supported)
          </label>
        </div>
      </div>

      <div style={{ marginTop: '30px' }}>
        <h3>Safe Confirmation</h3>
        <label>
          Confirmation Delay (seconds):
          <input
            type="number"
            min="0"
            max="30"
            value={settings.confirmationDelay}
            onChange={(e) => setConfirmationDelay(e.target.value)}
            style={{ marginLeft: '10px', padding: '5px', width: '80px' }}
          />
        </label>
      </div>

      <div style={{ marginTop: '30px' }}>
        <h3>Biometric Authentication</h3>
        <label>
          <input
            type="checkbox"
            checked={settings.enableBiometric}
            onChange={toggleBiometric}
            style={{ marginRight: '10px' }}
          />
          Enable Biometric (Demo)
        </label>
        <p style={{ marginTop: '8px', color: '#555' }}>
          Uses a simulated biometric check for demo purposes.
        </p>
      </div>

      <button 
        onClick={handleSave}
        disabled={saving}
        style={{ marginTop: '30px', padding: '15px 30px' }}
      >
        {saving ? 'Saving...' : 'Save Settings'}
      </button>

      <button 
        onClick={() => navigate('/dashboard')} 
        style={{ marginTop: '30px', padding: '15px 30px' }}
      >
        Back to Dashboard
      </button>
    </div>
  );
}

export default Settings;
