import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAccessibility } from '../contexts/AllContexts';

const AccessibilityControls = () => {
  const navigate = useNavigate();
  const {
    settings,
    toggleVoice,
    toggleHighContrast,
    toggleScreenReader,
    setFontSize
  } = useAccessibility();

  return (
    <div style={{ marginTop: '20px', padding: '12px', border: '1px solid #ddd', borderRadius: '6px' }}>
      <h3 style={{ marginTop: 0 }}>Quick Accessibility</h3>
      <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        <button onClick={toggleVoice}>
          {settings.enableVoiceNavigation ? 'Disable Voice' : 'Enable Voice'}
        </button>
        <button onClick={toggleHighContrast}>
          {settings.highContrastMode ? 'Normal Contrast' : 'High Contrast'}
        </button>
        <button onClick={toggleScreenReader}>
          {settings.enableScreenReader ? 'Screen Reader Off' : 'Screen Reader On'}
        </button>
        <select
          aria-label="Font size"
          value={settings.fontSize}
          onChange={(e) => setFontSize(e.target.value)}
        >
          <option value="small">Small</option>
          <option value="medium">Medium</option>
          <option value="large">Large</option>
          <option value="extra-large">Extra Large</option>
        </select>
        <button onClick={() => navigate('/settings')}>Open Settings</button>
      </div>
    </div>
  );
};

export default AccessibilityControls;
