// utils/notifySuccess.js
import { toast } from "react-toastify";

export const notifySuccess = (amount, speakEnabled, speak) => {
  const message = `Rupees ${amount} paid successfully on UPI Saathi`;

  // visual feedback
  toast.success(message);

  // voice feedback (only if enabled)
  if (speakEnabled && typeof speak === "function") {
    speak(message);
  }
};
